/**
 * Module dependencies
 */

var ImageMethods = require('./methods');
var extend = require('./util').extend;


/**
 * Module exports
 */

module.exports = Preset;


/**
 * Preset
 */

function Preset (name) {
    this.name = name;
    this.config = {};
    this.args = {};

    extend(this, ImageMethods);

    this.save = function (name) {
        name = this.name || name;

        var Image = require('./magician');
        Image.presets[name] = this;
    };
};